const NebulaDrop = require('./lib/nebula');
const NebulaWebSocket = require('./lib/websocket');
const { generateApiKey } = require('./lib/utils');
const { criarPairing, emparelhar } = require('./lib/pairing');
const { salvarSessao, carregarSessao } = require('./lib/session');

function connect({ apiKey }) {
    if (!apiKey) throw new Error('API key é obrigatória');
    return new NebulaDrop(apiKey);
}

module.exports = {
    connect,
    generateApiKey,
    NebulaWebSocket,
    criarPairing,
    emparelhar,
    salvarSessao,
    carregarSessao
};
