const fs = require('fs');
const path = require('path');
const sessionPath = path.join(__dirname, '../sessions.json');

function salvarSessao(sessions) {
    fs.writeFileSync(sessionPath, JSON.stringify(sessions, null, 2));
}

function carregarSessao() {
    if (!fs.existsSync(sessionPath)) return {};
    return JSON.parse(fs.readFileSync(sessionPath));
}

module.exports = { salvarSessao, carregarSessao };
